% ];
% bar3((R_change_theta))
% figure,
% bar3((R_change_phi))

clc
clear
close all
r = 1;
idx = 1;
theta = 0;
phi = pi/2;
for l = 5:5
    temp = 0;
    for m = -l:l
        if(m>=0)
            SH = spharm(l,m,theta,phi)
            temp = temp + SH;
            %Y_ml(l,r) =Y_ml(l,r) + spharm(l,m,pi/4,pi/4);
        else
            SH = (-1)^(-m) * conj(spharm(l,-m,theta,phi))
            temp = temp + SH;
            %Y_temp = spharm(l,-m,pi/4,pi/4);
            %Y_ml(l,r) =Y_ml(l,r) + (-1)^(-m) * conj(Y_temp);
        end
        X(idx) = real(SH);
        Y(idx) = imag(SH);
        idx = idx+1;
        X(idx) = 0;
        Y(idx) = 0;
        idx = idx+1;
    end
    Y_ml = temp;
    finalX = real(temp);
    finalY = imag(temp);
end
abs(Y_ml)
plot(X,Y,'o');hold on;
plot(X,Y);hold on;
plot(finalX,finalY,'ro')